// we're passing a "done" fn
// and we're returned an "abort" function
let recognize: (string => unit) => (unit => unit) =
[%bs.raw {|
  function(done) {
    var r = new webkitSpeechRecognition();
    r.continuous = false
    r.interimResults = false
    r.onresult = evt => {
      done(evt.results[0][0].transcript)
    }
    r.start()
    return () => r.abort()
  }
|}];
/* later */
let abort = recognise (fun recognizedText => {
  Js.log2 "You said: " recognizedText;
});
// and if you want to cancel it
abort ();

